import java.util.Scanner;public class BlogEntryTest {    public static void main(String[] args) {        Scanner getInput = new Scanner(System.in);        BlogEntry blogEntry1 = new BlogEntry();        blogEntry1.setUsername(getUsername(getInput));        blogEntry1.setTextEntry(getBlogEntry(getInput));        blogEntry1.setDate(getDate(getInput));        blogEntry1.displayEntry();        System.out.println("getSummary: " + blogEntry1.getSummary());        BlogEntry blogEntry2 = new BlogEntry("Bob", "hello", "9, 5, 2025");        blogEntry2.displayEntry();        System.out.println();    }    public static String getUsername(Scanner getInput) {        System.out.println("Input Username: ");        String userInput = getInput.nextLine();        return userInput;    }    public static String getBlogEntry(Scanner getInput) {        System.out.println("Input blog entry: ");        String userInput = getInput.nextLine();        return userInput;    }    public static String getDate(Scanner getInput) {        System.out.println("Input year: ");        String year = getInput.nextLine();        System.out.println("Input month: ");        String month = getInput.nextLine();        System.out.println("Input day: ");        String day = getInput.nextLine();        return (month + ", " + day + ", " + year);    }}